import React, {useEffect, useState} from 'react';

import Link from "next/link";
import MenuIntro from "../components/MenuIntro";
import CsCenter from "../components/CsCenter";
import clubArray from "../data/clubArray";
import Head from "next/head";

function mainSlider(){
    const slider = $('.mainSlider .content > ul').bxSlider({
        mode: 'horizontal',            // 슬라이드의 이동방향 설정 verticla,fade
        speed: 500, // m/s ex > 1000 = 1s
        easing: 'ease-in-out', // 동작 가속도 css와 동일
        sliderMargin: 10, // img 와 img 사이 간격
        startSlide: 0, // 시작시 로드될 이미지 (0부터 시작)
        preloadImages: 'visible', // 'visible'은 보여질때 이미지를 로드,'all'로 설정 하게 되면 모든 이미지가 로드되어야만 slide가 작동
        randomStart: false, // 시작시 랜덤으로 이미지 로드 여부 (boolean)
        adaptiveHeight: true, //각 이미지의 높이에 따라 슬라이더 높이의 유동적 조절 여부
        adaptiveHeightSpeed: 500, //adaptiveHeight 동작속도,
        video: false,// slider에 video 사용여부, 사용할 시에 plugins/jquery.fitvids.js 파일 include 필요
        captions: false, // img 태그에 title속성값을 출력여부, 단 css .bx-wrapper .bx-caption 수정필요

        //responsive method
        responsive: true, // 반응형 지원 여부
        touchEnabled: true,// 터치스와이프 기능 사용여부
        swipeThreshold: 50, // 터치하여 스와이프 할때 변환 효과에 소모되는 시간 설정
        onoToOneTouch: true, // fade효과가 아닌 슬라이드는 손가락의 접지상태에 따라 슬라이드를 움직일수있다.
        preventDefaultSwipeX: false, //onoToOneTouch 에서 true일 경우, 손가락을따라 x축으로 움직일지에 대한 여부
        preventDefaultSwipeY: false, //onoToOneTouch 에서 true일 경우, 손가락을따라 y축으로 움직일지에 대한 여부

        //control method
        controls: false, //좌, 우 컨트롤 버튼 출력  여부
        auto: true, // 자동 재생 활성화.
        autoControls: false, //자동재생 제어버튼 활성화 단, auto모드 활성화필요
        autoControlsCombine: false, // 재생시 중지버튼 활성화(toggle)
        pause: 4000, // 자동 재생 시 각 슬라이드 별 노출 시간
        autoStart: true, // 페이지 로드가 되면, 슬라이드의 자동시작 여부
        autoDirection: 'next', // 자동 재생시에 정순, 역순(prev) 방식 설정
        autoHover: true, // 슬라이드 오버시 재생 중단 여부 (false : 오버무시)
        autoDelay: 0, // 자동 재생 전 대기 시간 설정.
        infiniteLoop: true, //마지막에 도달 했을시, 첫페이지로 갈 것인가 멈출것인가
    });
}

const Index = ({windowSize}) => {
    const [isMobile,setIsMobile]=useState(false);

    const [pbaBtn,setPbaBtn] = useState("../images/pba-text-logo.png");

    const onMouseOverPBABtn = () => {
        setPbaBtn("../images/pba-text-logo02.png");
    }
    const onmouseleavePBABtn = () => {
        setPbaBtn("../images/pba-text-logo.png");
    }
    const clubList = () => {
        const result = [];
        clubArray.map((v,i) => (
            result[i] = {"menu":v[0],"link":`/subClub?place=${v[1]}`}
        ))
        return result;
    };

    useEffect(()=>{
        if(windowSize > 1023){
            setIsMobile(false);
            mainSlider()
        }else{
            setIsMobile(true)
        }
    },[windowSize,isMobile]);

    return(
        <>
            {isMobile ?
                null :
                <div>
                    <Head>
                        <script src="/js/jquery.bxslider.min.js"/>
                        <script src="/js/plugins/jquery.easing.1.3.js"/>
                        <script src="/js/plugins/jquery.fitvids.js"/>
                    </Head>
                </div>
            }

            <section className="mainIntro">
                <div className="content">
                    <h3>
                        당구의 모든것,<br/>
                        <img src="../images/pba-magazine-logo.png" alt="PBA매거진"/><br/>
                        에서 한눈에
                    </h3>
                    <div className="iframe">
                        <iframe width="746" height="420"
                                src="https://www.youtube.com/embed/qQ5S7pnMUrY" frameBorder="0"
                                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                allowFullScreen></iframe>
                    </div>
                    <span>
                                    <Link href="/mainPBA">
                                         <a  onMouseOver={onMouseOverPBABtn} onMouseLeave={onmouseleavePBABtn}>
                                            <img src={pbaBtn} alt="PBA"/>바로가기
                                         </a>
                                    </Link>
                            </span>
                </div>
                <div className="content">
                    <figure>
                        <img src="../images/main-bottom-bg.png" alt="모바일 파두스"/>
                    </figure>
                </div>
            </section>
            <section className="mainSlider">
                <div className="content">
                    <ul>
                        <li>
                            <MenuIntro
                                className={"storeMain"}
                                imgSrc={"../images/store-banner.png"}
                                imgAlt={"모바일 중고장터"}
                                title={<h3>개인큐 <span>중고</span>장터</h3>}
                                subMenuList = {[
                                    {menu:"판매글보기",link:"/subStoreSeller"},
                                    {menu:"안전거래",link:"/subStoreSafetyDeal"},
                                    {menu:"사기꾼검색",link:"/subStoreTrickster"}
                                ]}
                            />
                        </li>
                        <li>
                            <MenuIntro
                                className={"communityMain"}
