import React, {useEffect, useState} from 'react';
import PropTypes from 'prop-types';

import HeadTag from '../components/layout/HeadTag';
import Header from "../components/layout/Header";
import MoHeader from "../components/layout/MoHeader";
import Footer from "../components/layout/Footer";
import MoFooter from "../components/layout/MoFooter";
import Wrap from "../components/layout/Wrap";

import '../public/css/import.css';


const _app = ({ Component })=>{
    const [windowSize,setWindowSize]=useState(0);

    const handleResize = () =>{
        setWindowSize(window.innerWidth)
    }

    useEffect(()=>{
        window.addEventListener('resize',handleResize);
        handleResize();
    },[windowSize])
    return (
        <>
            <HeadTag/>
            {windowSize > 1023 ? <Header size={windowSize}/> : <MoHeader/>}
            <Wrap windowSize={windowSize}><Component windowSize={windowSize}/></Wrap>
            {windowSize > 1023 ? <Footer size={windowSize}/> : <MoFooter/>}
        </>
    );
};

_app.prototype = {
    Component: PropTypes.elementType.isRequired,
}

export default _app;
